import { parse } from 'node-html-parser';


const App = props => {

  useEffect(() => {
    const parsedHTML = parse(AvatarsApp);
    const scripts = parsedHTML.querySelectorAll('script');
    console.log(scripts)


    for (let i = 0; i < scripts.length; i++){
      const script = document.createElement("script");
      console.log(scripts[i].tagName)
      console.log(scripts[i].attributes ? scripts[i].attributes.src : "")
      script.src = `/WebXRApps/avatars-master/${scripts[i].attributes ? scripts[i].attributes.src : ""}`;
      script.async = false;
      document.body.appendChild(script);
    }

    // scripts.forEach(s => {
