    //   script.async = true;
    //   document.body.appendChild(script);
    // })



  })





  return (
    <div className="App">
      {/* <Navbar /> */}
      <WebXRApp html={{__html: AvatarsApp}} />
      {/* <WebXRApp html={{__html: BrowserApp}} /> */}
    </div>
  );
}

export default App;
